for (var i = 0 ; basket && i < basket.length ; i ++) {
    var basketItem = basket[i];
    var entity = requestDS.entities.item[requestDS.entities.item.length];

    // entity key
    var key = entity.key;
    key.applicationContext.URL = 'FET-CATALOG';
    key.type = basketItem.type;
    key.applicationDN = basketItem.id;

    fet_common.addCharacteristics(entity, basketItem);
}

return;
