script.script:
/*
* auther:ben
*/

Global.logDebug(input);
var addItemsRequest = input;
var addItemsResponse = new DataStructure('fet_on_api.addItemsResponse');

try {
    var orderHelper = new fet.common.ClsDataOrderHelper();
    var orderId = addItemsRequest.orderId;
    orderHelper.getOrderById(orderId);
    var offerCodes = addItemsRequest.offerCodes;

    for (var i = 0 ; offerCodes && i < offerCodes.length ; i ++) {
        var offerCode = offerCodes[i];
        orderHelper.addItem(offerCode);
    }

    addItemsResponse.orderId= orderHelper.order._id;
} catch (e) {
    addItemsResponse.errorMessage = e;
}

return addItemsResponse;
