script.script:
/*
* auther:karl
*/

Global.logDebug(input);
var createOrderRequest = input;
var createOrderResponse = new DataStructure('fet_on_api.createOrderResponse');

try {
    var orderHelper = new fet.common.ClsDataOrderHelper();
    orderHelper.createOrderAndBasket();
    var orderInstance = orderHelper.order.orderInstance;
    orderInstance.customerId = createOrderRequest.customerId;
    orderInstance.accountId = createOrderRequest.accountId;
    orderInstance.orderType = createOrderRequest.orderType;

    orderHelper.save();

    fet_on_api.setOrderDS(orderHelper.order, createOrderResponse.order);

} catch (e) {
    createOrderResponse.errorMessage = e.toString();
}

return createOrderResponse;
