script.script:
/*
* auther:karl
*/

Global.logDebug(input);
var getEligibleOffersRequest = input;
var getEligibleOffersResponse = new DataStructure('fet_on_api.getEligibleOffersResponse');
var hierarchyCode = getEligibleOffersRequest.hierarchyCode;
var itemType = getEligibleOffersRequest.itemType;
var date = getEligibleOffersRequest.date;

try {
    var catalogHelper = new fet_common.ClsCatalogHelper();
    var catalogItems = catalogHelper.listItems(hierarchyCode, itemType, date);

    for (var i = 0 ; catalogItems && i < catalogItems.length ; i ++) {
        getEligibleOffersResponse.item[i].code = catalogItems[i].code;
        getEligibleOffersResponse.item[i].label = catalogItems[i].getLabel();
    }

} catch (e) {
    getEligibleOffersResponse.errorMessage = e;
}

return getEligibleOffersResponse;
