try {
    var catalogHelper = new fet.common.ClsCatalogHelper();
    var hierarchyChildren = catalogHelper.getHierarchyChildren(hierarchyNodeCode);

    for (var i = 0 ; hierarchyChildren && i < hierarchyChildren.length ; i ++) {
        queryCatalogHierarchyResponse.hierarchyChildrenNode[i].code = hierarchyChildren[i];
    }

