script.script:
/*
* auther:karl
*/

Global.logDebug(input);
var queryOrderDetailsRequest = input;
var queryOrderDetailsResponse = new DataStructure('fet_on_api.queryOrderDetailsResponse');
var orderId = queryOrderDetailsRequest.orderId;

try{
    var orderHelper = new fet.common.ClsDataOrderHelper();
    orderHelper.getOrderById(orderId);

    queryOrderDetailsResponse.orderId = orderHelper.order._id;
    fet_on_api.setBasketDS(orderHelper.basket, queryOrderDetailsResponse.basket);

} catch(e) {
    queryOrderDetailsResponse.errorMessage = e.toString();
}

return queryOrderDetailsResponse;
