script.script:
/*
 * auther:karl
 */

var queryParingDataRequest = input;
var queryParingDataResponse = new DataStructure('fet_on_api.queryParingDataResponse');
var paringDataDocFinder = new Finder('fet.on.dao.paringDataDocFinder');
var paringDataDoc = new Document('fet.on.dao.paringDataDoc');
paringDataDoc.processId = queryParingDataRequest.processId;
paringDataDoc.MSISDN = queryParingDataRequest.MSISDN;
paringDataDoc.IMSI = queryParingDataRequest.IMSI;
paringDataDoc.ProductId = queryParingDataRequest.ProductId;

try {
    var paringDataDocList = paringDataDocFinder.search(null, paringDataDoc);
    for (var i = 0 ; i < paringDataDocList.length ; i ++) {
        var paringDataDoc = paringDataDocList[i];
        queryParingDataResponse.paringData[i].MSISDN = paringDataDoc.MSISDN;
        queryParingDataResponse.paringData[i].IMSI = paringDataDoc.IMSI;
        queryParingDataResponse.paringData[i].ProductId = paringDataDoc.ProductId;
    }
} catch (e) {
    queryParingDataResponse.errorMessage = e.toString();
}

return queryParingDataResponse;
