script.script:
/*
* auther:kevin
*/

Global.logDebug(input);
var removeItemsRequset = input;
var removeItemsResponse = new DataStructure('fet_on_api.removeItemsResponse');

try{
    var orderHelper = new fet.common.ClsDataOrderHelper();
    var orderId = removeItemsRequset.orderId;
    orderHelper.getOrderById(orderId);
    var basketItemIdList = removeItemsRequset.basketItemId;

    for (var i = 0 ; basketItemIdList && i < basketItemIdList.length ; i ++) {
        var basketItemId = basketItemIdList[i];
        orderHelper.removeItem(basketItemId);
    }

    removeItemsResponse.orderId = orderHelper.order._id;
    fet_on_api.setBasketDS(orderHelper.basket, removeItemsResponse.basket);

} catch(e) {
    removeItemsResponse.errorMessage = e.toString();
}

return removeItemsResponse;
