Global.logDebug(input);
var submitPreActivateOrderRequest = input;
var submitPreActivateOrderResponse = new DataStructure('fet_on_api.submitPreActivateOrderResponse');

var processDoc = new Document('fet_om.defaultProcessDoc');

try {
    processDoc.submitPreActivateOrderRequestJson = submitPreActivateOrderRequest.toJson();
    var processId = Process.startProcess('fet_om.defaultProcess', processDoc);
    submitPreActivateOrderResponse.processId = processId;

} catch (e) {
    submitPreActivateOrderResponse.errorMessage = e;
}

return submitPreActivateOrderResponse;
